import React, {Component} from 'react'

class Note extends Component {

  constructor(props){
    super(props)
    this.state ={
      editing : false
    }
    this.edit = this.edit.bind(this)
    this.remove = this.edit.bind(this)
  }
    edit() {
      this.setState({
        editing: true
      })
    }
    remove() {
      alert('Removing a note')
    }

  render(){
    return(
      <div className="note">
          <p>Learn React</p>
          <span>
            <button onClick={this.edit} id="edit">Edit</button>
            <button onClick={this.remove} id="remove">Remove</button>
          </span>
      </div>
    )
  }
}

export default Note
